        );
        $ex->exportTable('Conversation', "
         SELECT
         pm.*,
         g.title AS title2
       FROM forum_message_data pm
       JOIN z_pmgroup g
         ON g.group_id = pm.message_id;", $conversation_Map);

        // User Conversation.
        $userConversation_Map = array(
            'group_id' => 'ConversationID',
            'userid' => 'UserID'
        );
        $ex->exportTable('UserConversation', "
