                  'ipb' as HashMethod,
                  $showEmail as ShowEmail,
                  case when length(p.pp_main_photo) <= 3 or p.pp_main_photo is null then null
                     when p.pp_main_photo like '%//%' then p.pp_main_photo
                     else concat('{$cdn}ipb/', p.pp_main_photo)
