            where length(signature) > 1

            union

            select uid, 'Plugin.Signatures.Format', 'Markdown'
            from :_user
            where length(signature) > 1

            union

            select uid, 'Profile.Website' as name, website
            from :_user
            where length(website) > 7

            union

            select uid, 'Profile.Location' as name, location
            from :_user
            where length(location) > 1

