            from :_topic t
            left join :_post p
            on t.mainPid = p.pid
            left join z_reactiontotalsupvote u
            on u.value = t.mainPid
            left join z_reactiontotalsdownvote d
            on d.value = t.mainPid
            left join :_poll poll
            on p.tid = poll.tid
            where t.deleted != 1

