        ", $pollOption_Map);

        $pollVote_Map = array(
            'userid' => 'UserID',
            'poll_option_id' => 'PollOptionID'
        );

        $ex->exportTable('PollVote', "

            select povm.members as userid, po._num as poll_option_id
            from :_poll_options_votes__members povm
